import{_ as u}from"./artist_collector_process-f4f52f4a.js";import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{r as o,o as d,c as p,d as r,e,b as t,w as s,f as n}from"./app-de5b3524.js";const c={},v=r("p",null,[e("En tant qu'artiste, vous avez la possibilité de créer des œuvres d'art numériques et de recevoir un certificat numérique afin d'assurer votre propriété sur votre art. Vous pourrez ensuite vendre vos œuvres d'art sur la plateforme Monumental et percevoir des redevances sur toute vente future de votre travail."),r("br"),r("br"),r("br"),e(" Ce guide vise à expliquer les activités d'un artiste et comment les réaliser sur la plateforme.")],-1),m=r("h2",{id:"devenir-artiste-sur-monumental",tabindex:"-1"},[r("a",{class:"header-anchor",href:"#devenir-artiste-sur-monumental","aria-hidden":"true"},"#"),e(" Devenir artiste sur Monumental")],-1),f=r("p",null,"Pour devenir artiste sur la plateforme Monumental, vous avez soit été invité par un gestionnaire d'espace ou tout autre participant de la plateforme ayant le pouvoir d'envoyer une invitation, soit vous avez soumis une demande en ligne qui a été approuvée.",-1),g={href:"https://monumental.app/apply/artist",target:"_blank",rel:"noopener noreferrer"},h=n('<h2 id="apercu" tabindex="-1"><a class="header-anchor" href="#apercu" aria-hidden="true">#</a> Aperçu</h2><p>Le processus de mise en vente d&#39;une œuvre d&#39;art implique généralement plusieurs étapes clés. Celles-ci comprennent la création ou la sélection d&#39;une œuvre d&#39;art existante, l&#39;abonnement à un espace, la préparation de l&#39;œuvre d&#39;art pour la vente, sa publication et enfin le démarrage d&#39;une enchère, soit sous la forme d&#39;une enchère chronométrée, soit sous la forme d&#39;une vente à prix fixe.</p><figure><img src="'+u+'" alt="Processus artiste" tabindex="0" loading="lazy"><figcaption>Processus artiste</figcaption></figure><h2 id="creation-de-nft-sur-monumental" tabindex="-1"><a class="header-anchor" href="#creation-de-nft-sur-monumental" aria-hidden="true">#</a> Création de NFT sur Monumental</h2><p>Créer un NFT sur Monumental est aussi simple qu&#39;envoyer un e-mail. Cette fonctionnalité vous permet de créer un NFT et son certificat numérique associé en utilisant la technologie de la blockchain ETH (Ethereum).<br> Pour accéder à cette fonctionnalité, cliquez sur :<br> Icône de votre profil -&gt; Créer<br> En créant un NFT, des frais de réseau seront appliqués (ils peuvent varier), vous devrez donc disposer de suffisamment de fonds dans votre portefeuille numérique.</p><div class="hint-container info"><p class="hint-container-title">Note</p><p>Si vous ne trouvez pas l&#39;option &quot;Créer&quot; sous l&#39;icône de votre profil, il est probable que vous ne soyez pas défini en tant qu&#39;artiste sur la plateforme. Si vous pensez que vous devriez être considéré comme un artiste, veuillez contacter votre gestionnaire d&#39;espace.</p></div><p>Cliquez sur Créer pour créer votre nouvelle œuvre d&#39;art<br> Une page apparaît vous offrant les options de créer une Édition Standard ou une Édition Communautaire.<br> Pour plus d&#39;informations sur les éditions Standard et Communautaire, veuillez vous référer à la section <strong>Contrat intelligent Monumental</strong></p><p><strong>Création d&#39;une Édition Standard:</strong></p><ol><li>Chargez votre œuvre d&#39;art en utilisant le glisser-déposer ou sélectionnez le fichier sous l&#39;espace réservé à cet effet.</li><li>Remplissez le formulaire comme suit: <ul><li>Entrez un nom de collection, par défaut <strong>pas de collection</strong> - vous pouvez créer un nom de collection ou choisir une collection existante à laquelle votre œuvre d&#39;art sera associée.</li><li>Entrez un nom d&#39;édition (le nom de votre œuvre d&#39;art).</li><li>Entrez une description.</li><li>Entrez les <strong>Paramètres de l&#39;œuvre d&#39;art</strong>: <ul><li>Entrez un nombre maximum d&#39;exemplaires - si votre œuvre d&#39;art est une édition unique, définissez le nombre maximum à &quot;1&quot;, si votre œuvre d&#39;art est composée de plusieurs exemplaires (édition limitée), comme 5, définissez le nombre maximum à &quot;5&quot;.</li><li>Entrez les redevances - il s&#39;agit du pourcentage qui vous est garanti et que vous recevrez sur toute vente future de cette œuvre d&#39;art.</li><li>Choisissez le support dans la liste.</li><li>Entrez des mots-clés - des mots qui décrivent le mieux vos œuvres - appuyez sur &quot;Entrée&quot; de votre clavier pour enregistrer vos mots-clés.</li></ul></li><li>Entrez les <strong>Paramètres d&#39;attribut</strong>, cliquez sur &quot;+&quot; pour valider vos paramètres d&#39;attribut.</li></ul></li><li>Acceptez les conditions de l&#39;espace pour générer votre NFT.</li><li>Appuyez sur <strong>GÉNÉRER</strong></li></ol><br>',10),b=r("br",null,null,-1),z=r("br",null,null,-1),q=n('<p><strong>Création d&#39;une Édition Communautaire :</strong></p><ol><li>Chargez votre œuvre d&#39;art en utilisant le glisser-déposer ou sélectionnez le fichier sous l&#39;espace réservé à cet effet.</li><li>Remplissez le formulaire comme suit : <ul><li>Entrez un nom de collection, par défaut <strong>pas de collection</strong> - vous pouvez créer un nom de collection ou choisir une collection existante à laquelle votre œuvre d&#39;art sera associée.</li><li>Entrez un nom d&#39;édition (le nom de votre œuvre d&#39;art).</li><li>Entrez une description.</li><li>Entrez les <strong>Paramètres de l&#39;œuvre d&#39;art</strong> : <ul><li>Nombre maximum d&#39;exemplaires - si votre œuvre d&#39;art est une édition unique, définissez le nombre maximum à <strong>1</strong>, si votre œuvre d&#39;art est composée de plusieurs exemplaires (édition limitée), comme 5, définissez le nombre maximum à <strong>5</strong>.</li><li>Entrez les redevances - il s&#39;agit du pourcentage qui vous est garanti et que vous recevrez sur toute vente future de cette œuvre d&#39;art.</li><li>Choisissez le support dans la liste.</li><li>Entrez des mots-clés - des mots qui décrivent le mieux vos œuvres - appuyez sur <strong>Entrée</strong> de votre clavier pour enregistrer vos mots-clés.</li></ul></li><li>Entrez les <strong>Paramètres de la Communauté</strong> : <ul><li>Espace : Choisissez l&#39;espace où votre œuvre d&#39;art sera publiée.</li><li>Prix de base : entrez le prix minimum de votre œuvre d&#39;art afin qu&#39;elle puisse être mise en circulation ou vendue.</li><li>Prévente – Si vous souhaitez que votre œuvre d&#39;art soit d&#39;abord en vente privée, sélectionnez cette option. Vous devrez définir qui sont les personnes autorisées pour cette vente privée. Suivez les étapes ci-dessous : <ul><li>Limite de NFT par adresse : Définissez le nombre maximal de jetons qu&#39;une personne autorisée peut posséder.</li><li>Montant maximal de création : Définissez le montant maximal de jetons qu&#39;une personne autorisée peut créer.</li><li>Adresse à autoriser : Entrez l&#39;adresse publique ETH de la personne autorisée - Appuyez sur &quot;+&quot; pour enregistrer la personne. Répétez ce processus pour ajouter d&#39;autres personnes autorisées.</li></ul></li></ul></li><li>Entrez les <strong>Paramètres d&#39;attribut</strong>, cliquez sur &quot;+&quot; pour valider vos paramètres d&#39;attribut.</li></ul></li><li>Acceptez les conditions de l&#39;espace pour générer votre NFT.</li><li>Appuyez sur <strong>GÉNÉRER</strong><br><br><br> Une fenêtre de <strong>Traitement du certificat numérique</strong> apparaît, en appuyant sur <strong>GÉNÉRER</strong>, cela créera le certificat numérique garantissant votre propriété sur l&#39;œuvre d&#39;art.<br><br><br> Suivez les étapes :</li><li>Appuyez sur <strong>GÉNÉRER</strong></li><li>À l&#39;étape 3, <strong>En attente d&#39;approbation</strong>, vous devrez approuver via votre portefeuille numérique la transaction et les frais qui y sont appliqués.</li><li>Une fois approuvée, l&#39;étape 4 est atteinte &quot;Certificat numérique&quot;.</li><li>Un message de confirmation de la blockchain apparaîtra pour confirmer que le certificat numérique a été créé sur la blockchain ETH.</li><li>L&#39;étape 5 est atteinte, Bravo votre NFT est généré et prêt à être publié !</li><li>Appuyez sur <strong>FERMER</strong><br><br><br> Vous serez dirigé vers votre profil personnel sous <strong>CRÉÉ</strong> où vous trouverez votre nouveau NFT.</li></ol><h2 id="vendre-un-nft-sur-monumental" tabindex="-1"><a class="header-anchor" href="#vendre-un-nft-sur-monumental" aria-hidden="true">#</a> Vendre un NFT sur Monumental</h2><p>Pour qu&#39;un artiste vende son œuvre d&#39;art numérique sur Monumental, l&#39;artiste doit en être le propriétaire et elle doit être publiée dans un espace.</p><p><strong>Premier Marché et Deuxième Marché :</strong><br><br> Si un artiste vend une édition pour son propre compte (l&#39;artiste et le propriétaire du jeton sont les mêmes), elle sera disponible sur le premier marché et toutes les commissions et frais associés de l&#39;espace seront appliqués.<br> Si la vente est effectuée via une adresse Ethereum différente de celle de l&#39;artiste (l&#39;artiste et le propriétaire sont différents), l&#39;édition sera sur le deuxième marché, et toutes les commissions et frais associés de l&#39;espace du deuxième marché seront alors appliqués.</p><p>Vendre un NFT doit être fait dans la vue détaillée de l&#39;œuvre d&#39;art.<br> Depuis la vue détaillée de l&#39;œuvre d&#39;art :</p><ol><li>Sélectionnez l&#39;édition que vous souhaitez vendre sous <strong>Éditions</strong></li><li>Cliquez sur <strong>VENDRE</strong></li><li>La fenêtre <strong>Vendre une œuvre d&#39;art</strong> apparaît, si des contrats d&#39;abonnement actifs existent déjà pour cette édition, ils seront présentés au vendeur sous la section <strong>Vos contrats actifs sont affichés ci-dessous</strong></li><li>Dans la section <strong>Vos contrats actifs sont affichés ci-dessous</strong>, sélectionnez le contrat actif où vous souhaitez effectuer la vente</li></ol><div class="hint-container info"><p class="hint-container-title">Info</p><p>Note : Vous pouvez souscrire un nouvel abonnement dans un espace différent de ceux pour lesquels vous avez déjà des contrats actifs. En cliquant sur &quot;Nouvel abonnement&quot;, en remplissant le formulaire et en soumettant votre contrat d&#39;abonnement, une proposition sera envoyée au gestionnaire d&#39;espace de cet espace pour approbation ultérieure.</p></div><ol start="5"><li>Cliquez sur <strong>SUIVANT</strong></li><li>Les informations de votre contrat actif sont affichées, montrant le numéro de contrat, les commissions et frais appliqués, les redevances</li><li>Entrez le prix en ETH de la vente dans le champ &quot;Prix&quot;</li><li>Choisissez <strong>une enchère à prix fixe</strong> ou <strong>une enchère chronométrée</strong> (période de vente de 24 heures ou Autre : personnalisez votre période de vente).</li></ol>',9),E=n('<p><strong>Enchère à Prix Fixe</strong><br><br> 9. Cliquez sur <strong>SUIVANT</strong> <br></p><p><strong>Enchère Chronométrée</strong><br><br> 9.1 Pour une enchère chronométrée de 24 heures, appuyez sur <strong>24 H</strong> <br><br> 9.2 Pour une enchère chronométrée personnalisée, appuyez sur <strong>AUTRE</strong><br><br> 9.3 Entrez la date et l&#39;heure de début de votre enchère<br> 9.4 Entrez la date et l&#39;heure de fin de votre enchère<br><br> La durée de l&#39;enchère sera calculée en utilisant la date et l&#39;heure de début et de fin. Cette durée sera prise en compte à la première offre.<br><br> 9.5 Cliquez sur <strong>SUIVANT</strong><br></p><ol start="10"><li>Un récapitulatif de votre contrat intelligent de vente que vous allez générer est affiché<br></li><li>Acceptez les conditions de vente et cliquez sur <strong>PUBLIER</strong></li><li>Depuis votre portefeuille numérique, approuvez la transaction</li><li>Votre contrat intelligent de vente est créé</li><li>Le transfert de la propriété du contrat s&#39;effectue, approuvez la transaction via votre portefeuille numérique</li><li>Cliquez sur <strong>FERMER</strong> pour fermer la fenêtre</li><li>Votre œuvre d&#39;art est maintenant prête à être vendue avec un statut <strong>ANNULER</strong></li></ol><div class="hint-container warning"><p class="hint-container-title">Note</p><p>En cas de changement d&#39;avis et si vous décidez d&#39;annuler la vente, cette action générera des frais de réseau</p></div>',4),x=r("br",null,null,-1),N=n('<h2 id="acheter-un-nft-sur-monumental" tabindex="-1"><a class="header-anchor" href="#acheter-un-nft-sur-monumental" aria-hidden="true">#</a> Acheter un NFT sur Monumental</h2><p>En tant qu&#39;artiste, vous avez le droit d&#39;acheter tout NFT en vente (premier et deuxième marché) sur la plateforme, à condition bien sûr que vous ayez suffisamment de fonds dans votre portefeuille numérique pour le faire.<br> N&#39;importe où sur la plateforme, les œuvres d&#39;art sont affichées avec un statut spécifique indiquant si des éditions sont disponibles à la vente et quel est leur prix de vente en ETH.<br> Lors de la navigation sur la plateforme, pour acheter une œuvre d&#39;art en vente, vous devrez d&#39;abord accéder à la vue détaillée de l&#39;œuvre d&#39;art en cliquant simplement sur l&#39;image de l&#39;œuvre d&#39;art que vous souhaitez acheter.<br> La vue détaillée apparaît et le bouton <strong>ACHETER MAINTENANT</strong> est disponible pour acheter l&#39;œuvre d&#39;art</p><ol><li>Acceptez les conditions de vente</li><li>Appuyez sur <strong>ACHETER MAINTENANT</strong></li><li>Votre portefeuille numérique apparaît</li><li>Depuis votre portefeuille numérique, approuvez la transaction</li><li>La transaction de vente est terminée</li><li>Votre portefeuille a été débité du prix de vente, y compris les commissions et frais, les redevances et les frais de réseau</li><li>Le statut de l&#39;édition est <strong>VENDRE</strong><br><br><br> Vous êtes maintenant le nouveau propriétaire de l&#39;édition (l&#39;icône du propriétaire a été mise à jour), l&#39;artiste reste le même.<br><br> Pour vérifier la transaction sur Etherscan, depuis la vue détaillée, activez le bouton <strong>Afficher les détails</strong> sous l&#39;image de l&#39;œuvre d&#39;art.<br><br> Accédez à l&#39;onglet <strong>Propriétaires</strong> de la grille et cliquez sur le bouton noir <strong>TX</strong>. Cela vous dirigera vers Etherscan, où vous pourrez vérifier la transaction de cette action de vente.</li></ol><h2 id="envoyer-un-nft-sur-monumental" tabindex="-1"><a class="header-anchor" href="#envoyer-un-nft-sur-monumental" aria-hidden="true">#</a> Envoyer un NFT sur Monumental</h2><p>En tant que propriétaire d&#39;un NFT, vous pouvez envoyer une édition de ce NFT à une autre personne. Cela peut être fait si votre édition n&#39;est pas en cours d&#39;enchères.<br> Depuis la vue détaillée de votre œuvre d&#39;art, suivez ces étapes :</p><ol><li>Sélectionnez le jeton que vous souhaitez envoyer</li><li>Cliquez sur les options (...) sous la section <strong>Éditions</strong></li><li>Sélectionnez <strong>Envoyer l&#39;édition</strong></li><li>Une fenêtre apparaît, entrez une adresse publique ETH valide de la personne à qui vous souhaitez envoyer l&#39;édition sélectionnée</li><li>Appuyez sur <strong>ENVOYER</strong></li><li>Depuis votre portefeuille numérique, approuvez la transaction (frais de réseau)</li><li>Le nouveau propriétaire de l&#39;édition sera mis à jour</li><li>En tant qu&#39;ancien propriétaire, l&#39;état de l&#39;édition est mis à jour à <strong>FAIRE UNE OFFRE</strong></li><li>L&#39;édition est maintenant disponible à la vente sur le deuxième marché pour le nouveau propriétaire</li></ol><h2 id="bruler-un-nft-sur-monumental" tabindex="-1"><a class="header-anchor" href="#bruler-un-nft-sur-monumental" aria-hidden="true">#</a> Brûler un NFT sur Monumental</h2><p>En tant que propriétaire d&#39;un jeton, vous avez la possibilité de brûler (détruire) votre jeton.<br><br> Les conditions pour brûler un jeton sont les suivantes:<br></p><ul><li>Vous êtes le propriétaire de ce jeton.</li><li>Le jeton ne fait pas l&#39;objet d&#39;un processus de vente/enchère.<br><br><br><br><br> Brûler votre jeton le transférera vers un compte (adresses de portefeuille qui ne peuvent recevoir que le jeton brûlé) appelé adresses de type <strong>eater</strong>.<br><br> Le fait de brûler un jeton élimine efficacement votre jeton de l&#39;offre disponible et réduit le nombre en circulation.<br><br> Brûler un jeton générera des frais de transaction sur le réseau.<br><br> Depuis la vue détaillée de l&#39;œuvre, sélectionnez le jeton que vous possédez et que vous souhaitez brûler.</li></ul><ol><li>Cliquez sur <strong>Afficher les détails</strong>, la grille d&#39;informations apparaît.</li><li>En bas de l&#39;onglet <strong>INFORMATIONS</strong>, cliquez sur <strong>BRÛLER L&#39;ÉDITION</strong>.</li><li>Votre portefeuille numérique apparaît.</li><li>Approuvez la transaction.</li><li>Un message d&#39;événement vous informe que votre jeton a été supprimé.</li><li>Votre jeton est retiré de l&#39;édition globale.</li><li>La quantité maximale d&#39;édition du jeton sera réduite.</li></ol><h2 id="nft-interdit-sur-monumental" tabindex="-1"><a class="header-anchor" href="#nft-interdit-sur-monumental" aria-hidden="true">#</a> NFT interdit sur Monumental</h2><p>En tant que propriétaire d&#39;œuvres d&#39;art, votre œuvre peut être interdite dans un espace. Cette décision a été<br> prise par votre gestionnaire d&#39;espace et les raisons derrière cette décision peuvent être multiples (par exemple :<br> votre œuvre ne reflète pas l&#39;esprit créatif de l&#39;espace). Dans un tel cas, votre œuvre n&#39;est<br> plus publiée sur la plateforme.</p><div class="hint-container info"><p class="hint-container-title">Note :</p><p>Une interdiction ne peut être appliquée qu&#39;à une œuvre d&#39;art si cette œuvre n&#39;est pas en vente.</p></div>',13);function _(T,C){const a=o("ExternalLinkIcon"),i=o("RouterLink");return d(),p("div",null,[v,m,f,r("p",null,[r("a",g,[e("Demandez à devenir artiste"),t(a)])]),h,e(" Une fenêtre de **Traitement du certificat numérique** apparaît, en appuyant sur **GÉNÉRER**, cela créera le certificat numérique garantissant votre propriété sur l'œuvre d'art. "),b,e(` Suivez les étapes : 1. Appuyez sur **GÉNÉRER** 2. À l'étape 3, **En attente d'approbation**, vous devrez approuver via votre portefeuille (par exemple, Metamask) la transaction et les frais qui y sont appliqués. 3. Une fois approuvée, l'étape 4 est atteinte "Certificat numérique". 4. Un message de confirmation de la blockchain apparaîtra pour confirmer que le certificat numérique a été créé sur la blockchain ETH. 5. L'étape 5 est atteinte, Bravo votre NFT est généré et prêt à être publié ! 6. Appuyez sur **FERMER** `),z,e(" Vous serez dirigé vers votre profil personnel sous **CRÉÉ** où vous trouverez votre nouveau NFT. "),q,r("p",null,[e("Pour plus de détails sur les enchères, veuillez vous référer à "),t(i,{to:"/guides/snb_artwork.html"},{default:s(()=>[e("Vente et Achat d'œuvres d'art")]),_:1})]),E,r("p",null,[e("Tous les participants sur la plateforme verront votre œuvre d'art avec un statut indiquant que votre œuvre d'art est maintenant à vendre."),x,e(" Pour plus d'informations sur le statut de l'édition pendant une vente, veuillez vous référer à "),t(i,{to:"/guides/snb_artwork.html"},{default:s(()=>[e("Vente et Achat d'œuvres d'art")]),_:1})]),N,r("p",null,[e("Pour comprendre pleinement les impacts et les attentes d'une œuvre interdite, veuillez vous référer à la section "),t(i,{to:"/guides/ban.html"},{default:s(()=>[e("Principes d'interdiction")]),_:1}),e(".")])])}const V=l(c,[["render",_],["__file","artist.html.vue"]]);export{V as default};
